import React, { useEffect, useState } from 'react';
import { Download } from '@mui/icons-material';
import { Button, TextField, Menu, MenuItem } from '@mui/material';
import { DataGrid } from '@mui/x-data-grid';
import axios from 'axios';
import { jsPDF } from "jspdf";
import "jspdf-autotable";
import styles from '../styles/reportsSecre.module.css';
import { faMagnifyingGlass } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

const Report = () => {
  const [selectedMonth, setSelectedMonth] = useState('');
  const [selectedYear, setSelectedYear] = useState('');
  const [appointments, setAppointments] = useState([]);
  const [username, setUserName] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [anchorEl, setAnchorEl] = useState(null);
  useEffect(() => {
    const fetchAppointments = async () => {
      try {
        const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/admin/reports/appointments/`);
        setAppointments(response.data);
      } catch (error) {
        console.error('Error fetching appointments:', error);
      } finally {
        setLoading(false);
      }
    };
    fetchAppointments();
    fetchProfile()
  }, []);

  const fetchProfile = async () => {
    try {
      const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/admin/me/`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`  // Assuming token-based authentication
        }
      });
      const staff = response.data;
  
      // Extract and handle optional fields
      const firstName = staff.first_name || "";
      const middleInitial = staff.middle_name ? `${staff.middle_name.charAt(0)}.` : ""; // Get first letter + "."
      const lastName = staff.last_name || "";
      const suffix = staff.suffix ? ` ${staff.suffix}` : ""; // Add space before suffix if it exists
  
      // Construct full name properly
      const fullName = `${firstName} ${middleInitial} ${lastName}${suffix}`.trim().replace(/\s+/g, ' '); // Remove extra spaces
  
      setUserName(fullName);
    } catch (error) {
      console.error('Error fetching profile:', error);
      // navigate('/');
    }
  };
  

  const handleSearchChange = (event) => setSearchTerm(event.target.value);
  const handleStartDateChange = (event) => setStartDate(event.target.value);
  const handleEndDateChange = (event) => setEndDate(event.target.value);

  const filteredAppointments = appointments.filter(appointment => {
    const appointmentDate = new Date(appointment.appointmentDate);
    const start = startDate ? new Date(startDate) : null;
    const end = endDate ? new Date(endDate) : null;

    return (
      (!start || appointmentDate >= start) &&
      (!end || appointmentDate <= end) &&
      ((appointment.id && appointment.id.toString().includes(searchTerm.toLowerCase())) ||
        (appointment.fullName && appointment.fullName.toLowerCase().includes(searchTerm.toLowerCase())) ||
        (appointment.contactNumber && appointment.contactNumber.toString().includes(searchTerm.toLowerCase())) ||
        (appointment.age && appointment.age.toString().includes(searchTerm.toLowerCase())) ||
        (appointment.address && appointment.address.toLowerCase().includes(searchTerm.toLowerCase())) ||
        (appointment.sex && appointment.sex.toLowerCase().includes(searchTerm.toLowerCase())))
    );
  });

  // Header and Footer Images
  const headerImage = "data:image/jpeg;base64,";





  const footerImage = "data:image/jpeg;base64,"; // Base64 string or URL of the footer image

  const downloadTodayReport = () => {
    const pdf = new jsPDF();
    const today = new Date().toISOString().split('T')[0];

    const todayAppointments = appointments.filter(appointment => {
      return appointment.appointmentDate === today;
    });

    const tableColumn = ["ID", "Patient Name", "Staff Name", "Contact Number", "Request Date", "Purpose"];
    const tableRows = [];

    todayAppointments.forEach(appointment => {
      const appointmentData = [
        appointment.id,
        appointment.patientFullName,
        appointment.contactnumber,
        appointment.appointmentDate,
        appointment.purpose
      ];
      tableRows.push(appointmentData);
    });

    pdf.autoTable({
      head: [tableColumn],
      body: tableRows,
      startY: 35, // Adjusted to account for the title and spacing
      headStyles: {
        fillColor: [31, 72, 126], // Header background color (RGB)
        textColor: [255, 255, 255], // Header text color (white)
        fontStyle: 'bold', // Make header bold
      },
      bodyStyles: {
        fillColor: [240, 240, 240], // Alternating row background color (light gray)
        textColor: [0, 0, 0], // Body text color (black)
      },
      alternateRowStyles: {
        fillColor: [255, 255, 255], // Alternate row background color (white)
      },
    });



    const pageCount = pdf.internal.getNumberOfPages();
    const pageHeight = pdf.internal.pageSize.height; // Height of each page
    const pageWidth = pdf.internal.pageSize.width; // Width of each page
    const headerHeight = 30; // Height of the header background

    for (let i = 1; i <= pageCount; i++) {
      pdf.setPage(i); // Go to the specific page

      // Add header only to the first page
      if (i === 1) {
        // Add header background
        pdf.setFillColor(37, 99, 235);
        pdf.rect(0, 0, pageWidth, headerHeight, "F"); // Filled rectangle for the header

        // Add header logo
        if (headerImage) {
          const logoWidth = 30; // Logo width
          const logoHeight = 30; // Logo height
          const xPosition = 10; // X-position for the logo
          const yPosition = 0; // Y-position for the logo
          pdf.addImage(headerImage, "PNG", xPosition, yPosition, logoWidth, logoHeight); // Add the logo
        }

        // Add header text
        pdf.setFont("bold");
        pdf.setFontSize(30); // Font size
        pdf.setTextColor(255, 255, 255); // White text color
        pdf.text("Clinic Report", pageWidth / 2, 18, { align: "center" }); // Centered title
      }

      // Add footer to every page
      if (footerImage) {
        pdf.addImage(footerImage, "PNG", -10, pageHeight - 10, 250, 10); // Footer image
      }

      // Add footer text
      pdf.setTextColor(0, 0, 0); // White text color
      pdf.setFontSize(10);
      pdf.text(`Page ${i} of ${pageCount}`, 14, pageHeight - 10); // Footer page number
      pdf.text(
        "Queue Care - Confidential Report",
        pageWidth / 2,
        pageHeight - 10,
        { align: "center" }
      ); // Footer text centered
    }

    // Define the total number of appointments
    const totalAppointments = todayAppointments.length;

    // Add "Generated by" and "Total Appointments" at the top-right corner
    const contentStartY = 10; // Start just below the header
    const rightMargin = 10; // Space from the right edge of the page

    pdf.setFont("bold");
    pdf.setFontSize(7);
    pdf.setTextColor(255, 255, 255); // BlacWhitek text color
    // Right-aligned text

    pdf.text(`Total Appointments: ${totalAppointments}`, pageWidth - rightMargin, contentStartY + 5, { align: "right" });
    pdf.text(`Date: ${today}`, pageWidth - rightMargin, contentStartY + 5 + 5, { align: "right" });

    // Save the PDF
    pdf.save("appointment_report.pdf");
  }


  const downloadPDF = () => {
    const pdf = new jsPDF();

    // // Define the range of appointment dates
    // const startDate = filteredAppointments.length > 0
    //   ? new Date(Math.min(...filteredAppointments.map(a => new Date(a.date).getTime()))).toLocaleDateString()
    //   : "N/A";
    // const endDate = filteredAppointments.length > 0
    //   ? new Date(Math.max(...filteredAppointments.map(a => new Date(a.date).getTime()))).toLocaleDateString()
    //   : "N/A";




    // Table content
    const tableColumn = ["ID", "Patient Name", "Contact Number", "Appointment Date", "Purpose"];
    const tableRows = [];

    filteredAppointments.forEach(appointment => {
      const appointmentData = [
        appointment.id,
        appointment.patientFullName,
        appointment.contactnumber,
        appointment.appointmentDate,
        appointment.purpose
      ];
      tableRows.push(appointmentData);
    });

    pdf.autoTable({
      head: [tableColumn],
      body: tableRows,
      startY: 35, // Adjusted to account for the title and spacing
      headStyles: {
        fillColor: [48, 100, 52], // Header background color (RGB)
        textColor: [255, 255, 255], // Header text color (white)
        fontStyle: 'bold', // Make header bold
      },
      bodyStyles: {
        fillColor: [240, 240, 240], // Alternating row background color (light gray)
        textColor: [0, 0, 0], // Body text color (black)
      },
      alternateRowStyles: {
        fillColor: [255, 255, 255], // Alternate row background color (white)
      },
    });


    // Calculate the appointment date range
    const startDate = filteredAppointments.length > 0
      ? new Date(Math.min(...filteredAppointments.map(a => new Date(a.date).getTime()))).toLocaleDateString()
      : "N/A";
    const endDate = filteredAppointments.length > 0
      ? new Date(Math.max(...filteredAppointments.map(a => new Date(a.date).getTime()))).toLocaleDateString()
      : "N/A";


    const pageCount = pdf.internal.getNumberOfPages();
    const pageHeight = pdf.internal.pageSize.height; // Height of each page
    const pageWidth = pdf.internal.pageSize.width; // Width of each page
    const headerHeight = 30; // Height of the header background



    for (let i = 1; i <= pageCount; i++) {
      pdf.setPage(i); // Go to the specific page

      // Add header only to the first page
      if (i === 1) {
        // Add header background
        pdf.setFillColor(48, 100, 52); // Green color
        pdf.rect(0, 0, pageWidth, headerHeight, "F"); // Filled rectangle for the header

        // Add header logo
        if (headerImage) {
          const logoWidth = 30; // Logo width
          const logoHeight = 30; // Logo height
          const xPosition = 10; // X-position for the logo
          const yPosition = 0; // Y-position for the logo
          pdf.addImage(headerImage, "PNG", xPosition, yPosition, logoWidth, logoHeight); // Add the logo
        }

        // Add header text
        pdf.setFont("bold");
        pdf.setFontSize(30); // Font size
        pdf.setTextColor(255, 255, 255); // White text color
        pdf.text("Clinic Report", pageWidth / 2, 18, { align: "center" }); // Centered title
      }

      // Add footer to every page
      if (footerImage) {
        pdf.addImage(footerImage, "PNG", -10, pageHeight - 10, 250, 10); // Footer image
      }

      // Add footer text
      pdf.setTextColor(0, 0, 0); // White text color
      pdf.setFontSize(10);
      pdf.text(`Page ${i} of ${pageCount}`, 14, pageHeight - 10); // Footer page number
      pdf.text(
        "Clinic Report - Confidential Report",
        pageWidth / 2,
        pageHeight - 10,
        { align: "center" }
      ); // Footer text centered
    }

    // Define the total number of appointments
    const totalAppointments = filteredAppointments.length;

    // Add "Generated by" and "Total Appointments" at the top-right corner
    const contentStartY = 10; // Start just below the header
    const rightMargin = 10; // Space from the right edge of the page

    pdf.setFont("bold");
    pdf.setFontSize(7);
    pdf.setTextColor(255, 255, 255); // BlacWhitek text color
    // Right-aligned text
    pdf.text(`Generated by: ${username}`, pageWidth - rightMargin, contentStartY, { align: "right" });
    pdf.text(`Total Appointments: ${totalAppointments}`, pageWidth - rightMargin, contentStartY + 5, { align: "right" });
    pdf.text(`Date: ${startDate} - ${endDate}`, pageWidth - rightMargin, contentStartY + 5 + 5, { align: "right" });



    // Save the PDF
    pdf.save("appointment_report.pdf");
  }
  const downloadCSV = () => {
    // Calculate the appointment date range
    const startDate = filteredAppointments.length > 0
      ? new Date(Math.min(...filteredAppointments.map(a => new Date(a.date).getTime()))).toLocaleDateString()
      : "N/A";
    const endDate = filteredAppointments.length > 0
      ? new Date(Math.max(...filteredAppointments.map(a => new Date(a.date).getTime()))).toLocaleDateString()
      : "N/A";

    // Define the report title and date range
    const titleReport = "Appointments Report";
    const dateRangeInfo = `Appointment Date Range: ${startDate} - ${endDate}`;

    // Define headers and rows
    const headers = ["ID", "Patient Name", "Contact Number", "Appointment Date", "Purpose"];
    const rows = filteredAppointments.map(appointment => [
      appointment.id,
      appointment.patientFullName,
      appointment.contactNumber,
      appointment.appointmentDate,
      appointment.purpose
    ]);

    // Construct CSV content with title, date range, headers, and rows
    const csvContent =
      "data:text/csv;charset=utf-8," +
      `${titleReport}\n` + // Add the title as the first line
      `${dateRangeInfo}\n\n` + // Add the date range on the second line, followed by a blank line
      [headers, ...rows].map(e => e.join(",")).join("\n"); // Add headers and data rows

    // Create download link
    const encodedUri = encodeURI(csvContent);
    const link = document.createElement("a");
    link.setAttribute("href", encodedUri);
    link.setAttribute("download", "appointments_report.csv");
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };


  const handleClick = (event) => setAnchorEl(event.currentTarget);
  const handleClose = () => setAnchorEl(null);

  const columns = [
    { field: 'id', headerName: 'ID', width: 65 },
    { field: 'patientFullName', headerName: 'Name', width: 250 },
    {
      field: 'purpose',
      headerName: 'Purpose',
      width: 220,
    },
    { field: 'status', headerName: 'Status', width: 150 },
    { field: 'contactnumber', headerName: 'Contact Number', width: 90 },
    { field: 'appointmentDate', headerName: 'Appointment Date', width: 200 },
  ];


  // Handle report download with startDate and endDate
  const downloadCompletedReport = async () => {
    if (!startDate || !endDate) {
      alert("Please select a start and end date.");
      return;
    }

    try {
      // API Request
      const response = await axios.post(`${process.env.REACT_APP_API_URL}/secretary/reports/completed-appointment`, {
        startDate,
        endDate,
      });

      const reportData = response.data;
      if (!reportData || reportData.length === 0) {
        alert('No data found for the selected date range.');
        return;
      }

      // Generate CSV content
      let csvContent = "Date,Completed Appointments\n"; // CSV header
      reportData.forEach(row => {
        csvContent += `${row.date},${row.completed_count}\n`;
      });

      // Create a hidden link to trigger CSV download
      const encodedUri = encodeURI(`data:text/csv;charset=utf-8,${csvContent}`);
      const csvLink = document.createElement('a');
      csvLink.setAttribute('href', encodedUri);
      csvLink.setAttribute('download', `completed_appointments_${startDate}_${endDate}.csv`);
      document.body.appendChild(csvLink);
      csvLink.click();
      document.body.removeChild(csvLink);

    } catch (error) {
      console.error("Error downloading report:", error);
      alert("An error occurred while downloading the report. Please try again.");
    }
  };



  // Handle report download
  const downloadMonthlyReport = async () => {
    if (!selectedMonth || !selectedYear) {
      alert("Please select both month and year.");
      return;
    }

    try {
      // API Request
      const response = await axios.post(`${process.env.REACT_APP_API_URL}/secretary/reports/monthlyCompletedAppointment`, {
        month: selectedMonth,
        year: selectedYear
      });

      const reportData = response.data;
      if (!reportData || reportData.length === 0) {
        alert('No data found for the selected month and year.');
        return;
      }

      // Generate CSV content
      let csvContent = "Date,Completed Appointments\n"; // CSV header
      reportData.forEach(row => {
        csvContent += `${row.date},${row.completed_count}\n`;
      });

      // Create a hidden link to trigger CSV download
      const encodedUri = encodeURI(`data:text/csv;charset=utf-8,${csvContent}`);
      const csvLink = document.createElement('a');
      csvLink.setAttribute('href', encodedUri);
      csvLink.setAttribute('download', `monthly_completed_appointments_${selectedMonth}_${selectedYear}.csv`);
      document.body.appendChild(csvLink);
      csvLink.click();
      document.body.removeChild(csvLink);

    } catch (error) {
      console.error("Error downloading report:", error);
      alert("An error occurred while downloading the report. Please try again.");
    }
  };

  // Generate dropdown options for months and years
  const months = [
    { value: 1, label: 'January' },
    { value: 2, label: 'February' },
    { value: 3, label: 'March' },
    { value: 4, label: 'April' },
    { value: 5, label: 'May' },
    { value: 6, label: 'June' },
    { value: 7, label: 'July' },
    { value: 8, label: 'August' },
    { value: 9, label: 'September' },
    { value: 10, label: 'October' },
    { value: 11, label: 'November' },
    { value: 12, label: 'December' },
  ];

  const years = Array.from({ length: 30 }, (_, i) => new Date().getFullYear() - i); // 

  return (
    <div className={styles.doctorsSection}>
      <div className={styles.doctorsHeader}>
        <div>
          <h2 style={{ fontSize: '35px' }}>Report</h2>
          <p style={{ marginLeft: '10px', marginTop: '7px' }}>This is the latest update for todays report. Check now!</p>
        </div>
        <div>
          <Button onClick={downloadTodayReport} variant="contained" color="primary" startIcon={<Download />}>
            Download Today's Report
          </Button>


          {/* <Menu anchorEl={anchorEl} open={Boolean(anchorEl)} onClose={handleClose}>
            <MenuItem onClick={() => { downloadPDF(); handleClose(); }}>As PDF</MenuItem>
            <MenuItem onClick={() => { downloadCSV(); handleClose(); }}>As CSV</MenuItem>
            <MenuItem onClick={() => { downloadMonthlyReport(); handleClose(); }}>Download Monthly Completed Appointments Report</MenuItem>
          </Menu> */}
        </div>
      </div>

      {/* Search Field and Date Filters */}
      <div className={styles.searchAppointment}>
          <div className={styles.actionButtons} style={{
                              position: 'relative',
                              display: 'flex',
                              alignItems: 'center',
                              justifyContent: 'center', /* Center the content horizontally */
                              margin: 0, /* Remove default margin */
                              gap: '12px',
                              width: '100%'
            }}>
          <FontAwesomeIcon icon={faMagnifyingGlass} style={{
                              position: 'absolute',
                              left: '22px',
                              color: '#aaa'
                            }} />
          <input
            type="text"
            placeholder="Search"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            style={{
              paddingLeft: '50px'
            }}
          />
          <TextField
            label="Start Date"
            type="date"
            variant="outlined"
            value={startDate}
            onChange={handleStartDateChange}
            InputLabelProps={{ shrink: true }}
          />
          <TextField
            label="End Date"
            type="date"
            variant="outlined"
            value={endDate}
            onChange={handleEndDateChange}
            InputLabelProps={{ shrink: true }}
          />
          <div>
            <Button style={{ height: '98%' }} onClick={handleClick} variant="contained" color="primary">
              <Download />
            </Button>

            <Menu anchorEl={anchorEl} open={Boolean(anchorEl)} onClose={handleClose}>
              <MenuItem onClick={() => { downloadPDF(); handleClose(); }}>As PDF</MenuItem>
              <MenuItem onClick={() => { downloadCSV(); handleClose(); }}>As CSV</MenuItem>
              <MenuItem onClick={() => { downloadCompletedReport(); handleClose(); }}>Completed Appointment Report</MenuItem>
            </Menu>
          </div>
        </div>


        {/* DataGrid Table */}
        <div style={{ height: '100%', width: '100%', marginTop: '1%' }}>
          <DataGrid sx={{ height: 600, width: '100%', cursor: 'pointer' }}
            rows={filteredAppointments}
            columns={columns}
            pageSize={10}
            loading={loading}
            disableColumnFilter={false}
            disableColumnMenu={false}
          />
        </div>
      </div>
    </div>
  );
};

export default Report;
